@import "colors";
@import "fonts";

html {
  box-sizing: border-box;
  font-size: 100%;
}

body {
  margin: 0;
  font-family: 'Open Sans', sans-serif;
  font-size: 0.875rem;
  line-height: 1.5;
  color: #fff;
}

h1, h2 {
  margin: 0;
  font-family: 'Raleway', sans-serif;
}

.heading-big {
  font-size: 2.5rem;
}

p {
  margin: 0;
}

a {
  text-decoration: none;
  color: inherit;
}

.cta {
  position: relative;
  padding: 14px 77px;
  font-size: 1.0625rem;
  font-weight: 700;
  color: #fff;
  background: linear-gradient(to left, $blue, $cyan);
  border: none;
  border-radius: 28px;
  outline: none;
  overflow: hidden;
  cursor: pointer;

  span {
    position: relative;
  }

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(to left, $cyan, $blue);
    opacity: 0;
    transition: opacity 0.3s;
  }

  &:hover::before {
    opacity: 1;
  }
}


header, section, footer {
  padding: 0 calc((100vw - 1302px) / 2);
}

header {
  display: flex;
  justify-content: space-between;
  padding-top: 70px;
  background-color: $dark-blue-1;

  .logo {
    width: 168px;
  }

  nav {
    font-family: 'Raleway', sans-serif;
    font-size: 1rem;

    a:not(:last-child) {
      margin-right: 56px;
    }
  }
}

section {
  padding-bottom: 140px;

  &:nth-child(1) {
    position: relative;
    padding-top: 56px;
    text-align: center;
    background-color: $dark-blue-1;

    & > * {
      display: block;
      position: relative;
      z-index: 1;
      margin: 0 auto;
    }

    h1 {
      max-width: 28ch;
      margin: 28px auto;
    }

    p {
      max-width: 52ch;
      margin-bottom: 28px;
      font-size: 1.25rem;
    }

    .bg {
      position: absolute;
      width: 100%;
      left: 0;
      bottom: -70px;
      z-index: 0;
    }
  }

  &:nth-child(2) {
    padding-top: 35px;
    background: linear-gradient($dark-blue-1 7%, $dark-blue-2 7%);

    .container {
      display: grid;
      grid-template-columns: 1fr 1fr;
      grid-column-gap: 140px;
      grid-row-gap: 91px;
      max-width: 840px;
      margin: 0 auto;

      & > * {
        display: flex;
        flex-direction: column;
        align-items: center;
        position: relative;
        text-align: center;

        // make this icon a bit smaller
        &:last-child svg {
          height: 70px;
          margin-bottom: 7px;
        }
      }
    }

    svg {
      height: 77px;
    }

    h2 {
      margin: 28px 0 7px;
    }
  }

  &:nth-child(3) {
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-column-gap: 56px;
    align-items: center;
    background-color: $dark-blue-2;

    img {
      margin-left: auto;
    }

    div {
      padding-top: 56px;
    }

    h2 {
      max-width: 14ch;
      margin-bottom: 21px;
      line-height: 1.2;
    }

    p, a {
      font-size: 1rem;
    }

    p {
      max-width: 56ch;
      margin-bottom: 21px;
    }

    a {
      display: inline-flex;
      align-items: center;
      padding-bottom: 3px;
      color: $cyan;
      border-bottom: 1px solid $cyan;

      svg {
        width: 16px;
        margin-left: 4px;
        margin-bottom: -4px;
        transition: transform 0.15s;
      }

      &:hover svg {
        transform: translateX(2px);
      }
    }

  }

  &:nth-child(4) {
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    grid-column-gap: 42px;
    padding-top: 35px;
    background-color: $dark-blue-2;

    .quote-symbol {
      position: absolute;
      transform: translate(-14px, -21px);
    }

    .item {
      padding: 42px 28px 28px;
      background-color: $dark-blue-4;
      border-radius: 7px;

      & > p {
        font-size: 0.9375rem;
        line-height: 1.4;
      }
    }

    .author {
      display: flex;
      align-items: center;
      margin-top: 21px;

      img {
        width: 28px;
        margin-right: 14px;
        border-radius: 50%;
      }

      p {
        font-size: 0.6875rem;
        & > * {
          display: block;
        }

        strong {
          margin-bottom: 2px;
        }
      }
    }
  }

  &:nth-child(5) {
    padding-top: 56px;
    background: linear-gradient($dark-blue-2 45%, $dark-blue-3 45%);

    .card {
      display: flex;
      flex-direction: column;
      align-items: center;
      max-width: 721px;
      padding: 42px 70px;
      margin: 0 auto;
      text-align: center;
      background-color: $dark-blue-1;
      border-radius: 7px;
    }

    h2 {
      font-size: 2rem;
    }

    p {
      max-width: 84ch;
      margin: 14px 0 35px;
      font-size: 0.875rem;
    }

    form {
      display: grid;
      grid-template-columns: 1fr auto;
      grid-column-gap: 28px;
      width: 100%;
    }

    input, .cta {
      padding: 14px 28px;
    }

    .cta {
      font-size: 0.9375rem;
    }

    input[type=email] {
      outline: none;
      border: none;
      border-radius: 28px;
    }
  }
}

footer {
  padding-bottom: 98px;
  background-color: $dark-blue-3;

  .grid {
    display: grid;
    grid-template-columns: 4fr 2fr 1fr 1fr 2fr;
    grid-column-gap: 70px;

    svg {
      height: 17px;
    }

    a:not(:last-child) {
      margin-bottom: 14px;
    }

    & > * {
      &:nth-child(1) {
        display: grid;
        grid-template-columns: auto auto;
        grid-column-gap: 21px;

        svg {
          margin-top: 7px;
        }
      }

      &:nth-child(2) {
        & > * {
          display: flex;
          align-items: center;
        }

        svg {
          margin-right: 21px;
        }
      }

      &:nth-child(3), &:nth-child(4) {
        a {
          display: block;
        }
      }
    }
  }
}
